items.css:
.card {
 min-width: 150px;
 box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
}

.card figure {
 margin: 0;
}

.card figure img {
 width: 100%;
}

.card div {
 display: flex;
 flex-direction: column;
 gap: 8px;
 padding: 8px 8px 12px 8px;
}

.card div h4 {
 font-size: 16px;
 margin: 0;
}

.card div p {
 font-size: 16px;
 margin: 0;
}

.tree {
 min-width: 150px;

 display: flex;
 flex-direction: column;
 gap:12px;
}

.tree h4 {
 margin: 0;
 font-size: 24px;
 font-weight: bold;
}

.tree ul {
 list-style: none;
 margin: 0;
 padding: 0;

 display: flex;
 flex-direction: column;
 gap: 4px;
}

.tree li {
 display: flex;
 flex-direction: column;
 gap: 4px;

 cursor: pointer;
 color: gray;
}

.tree li:hover {
 color: black;
}

.tree li.active {
 color: black;
}

.tree li ul {
 margin-left: 12px;
}

.space {
 min-width: 150px;

 width: 100%;
 height: 100%;

 display: flex;
 align-items: center;
 justify-content: center;

 padding: 24px 0;

 border: 1px solid #ccc;
}
