items.css:
.essential-button-active {
    border: 1px solid #1B98E0;
    padding: 4px 20px;
    border-radius: 9999px;
    background: #1B98E0;
    color: white;
    width: 100%;
}

.essential-card {
 min-width: 150px;
 box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
 margin: 0;
}

.essential-card img {
 width: 100%;
}

.essential-card .text-wrap {
 display: flex;
 flex-direction: column;
 gap: 8px;
 padding: 8px 8px 12px 8px;
}

.essential-card .text-wrap h4 {
 font-size: 16px;
 margin: 0;
}

.essential-card .text-wrap p {
 font-size: 16px;
 margin: 0;
}

.card-horizontal {
    display: flex;
    align-items: center;
    gap: 20px;
    border: 1px solid #ccc;
    border-radius: 12px;
    overflow: hidden;
}

.card-horizontal .main-image {
    width: 150px;
}
